async function main() {
    var promises = []
    var clicks = Number(msg.payload)
    const now = new Date(msg.now)
    var waterDateLastSeen = getAndUpdateLastSeenDate("waterDateLastSeen", now)
    //check for new day
    var whatsNew = getWhichPeriodsAreNew(now, waterDateLastSeen)
    //check for incomplete today/lastday
    var [todayIncomplete, lastSeenIncomplete] = getWhichDaysIncomplete(whatsNew, now, waterDateLastSeen)

    if(whatsNew.newDay){
        try {
            promises.push(handleNewDay(whatsNew, waterDateLastSeen, lastSeenIncomplete))
        } catch(error) {
            console.error(error)
        }
    }

    identifySources(now, clicks)
    node.send([null, null, { sources: flow.get("currentlyRunningSources") }])

    //update database with flow, gallon
    if(msg.payload !== 0 || todayIncomplete  || whatsNew.newDay) { //if newday -> need to have a day doc even if there is no flow during that day
        promises.push(updateDatabaseRecordAllTotal(now, todayIncomplete))
    } else {
        //send zero as flow data, this is used as trigger for getting today's prediction
        node.send([{now: now, payload: 0}, null, null])
    }

    try {
        await Promise.all(promises)
    } catch(error) {
        console.error(error)
    }
    node.done()
}

async function updateDatabaseRecordAllTotal(now, todayIncomplete){
    //search for today's doc
    var start = new Date(now.valueOf())
    var end = new Date(start.valueOf())
    start.setHours(0,0,0,0)
    end.setHours(24,0,0,0)
    var doc = {
        $setOnInsert: { 
            created: now
        },
        $currentDate: {modified: true}
    }
    if(msg.payload !== 0){
        doc.$inc =  { allTotal: flow.get('clicksToGallons')(msg.payload) },
        doc.$push = { timeRecord: { clicks: msg.payload, created: now, sources: flow.get("currentlyRunningSources") }}
    } else { //in case non-zero flow is triggering this new database entry (i.e. on a new day), we don't conflict with $inc.allTotal
        doc.$setOnInsert.allTotal = 0
    }
    if(todayIncomplete){
        doc.$set = { incomplete : true }
    } else { 
        doc.$set = { incomplete : false } 
    }
    try {
        await flow.get('waterDbDailyColl').updateOne({created: { $gte:start, $lt:end }}, doc, {upsert:true, w: 1})
    } catch(error) {
        console.error(error)
    }
    node.send([{now: now, payload: flow.get('clicksToGallons')(msg.payload)*60}, null, null])
}

