}

function getWhichPeriodsAreNew(now, lastSeen){
    var whatsNew = {newDay: false, nowDate: now, lastSeenDate: lastSeen}
    if (!(lastSeen.getDate() == now.getDate() &&
        lastSeen.getMonth() == now.getMonth() &&
        lastSeen.getFullYear() == now.getFullYear())){
        whatsNew.newDay = true
        if(now.getDay()===1){ //if 1, then it's a Monday, so new week
            whatsNew.newWeek = true
        }
        if(lastSeen.getMonth() !== now.getMonth()){
            whatsNew.newMonth = true
            if(lastSeen.getFullYear() !== now.getFullYear()){
                whatsNew.newYear = true
            }
        }
    }
    return whatsNew
}

function getWhichDaysIncomplete(whatsNew, now, lastSeen){
    var todayIncomplete = false
    var lastSeenIncomplete = false
    if((now.getTime()-lastSeen.getTime())>flow.get("TimeoutMs")){
        //if more than timeout since midnight
        var lastMidnight = new Date(now.getTime())
        lastMidnight.setHours(0,0,0,0)
        if ((now.getTime()-lastMidnight.getTime())>flow.get("TimeoutMs")){
            todayIncomplete = true
        }
        if (whatsNew.newDay) {
            var dateLastSeenMidnight = new Date(lastSeen.getTime())
            dateLastSeenMidnight.setHours(24,0,0,0)
            if((dateLastSeenMidnight.getTime()-lastSeen.getTime())>flow.get("TimeoutMs")){
                lastSeenIncomplete = true
            }
        }
    }
    return [todayIncomplete, lastSeenIncomplete]
}

async function handleNewDay(whatsNew, lastSeenDate, lastSeenIncomplete){
    if(lastSeenIncomplete){
        // update last seen doc to incomplete
        var start = new Date(lastSeenDate.getTime())
        var end = new Date(start.getTime())
        start.setHours(0,0,0,0)
        end.setHours(24,0,0,0)
        var doc = {
            $set: { incomplete : true },
            $currentDate: {modified: true}
        }
        try {
            var promise = flow.get('waterDbDailyColl').update({created: { $gte:start, $lt:end }}, doc, {w: 1}).catch((e) => { console.error(e) })
        } catch(error){
            console.error(error)
        }
        node.send([null, {payload: whatsNew}, null])
    } else {
        node.send([null, {payload: whatsNew}, null])
    }
    try {
        await promise
    } catch(error) {
        console.error(error)
    }
}

function identifySources(now, clicks){
    //console.log("identifySources(...)")
    //console.log("clicks: "+clicks+", clicksPreviousSecond: "+flow.get("clicksPreviousSecond")+", margin: "+(flow.get("currentlyRunningSources").length || 1)*flow.get("driftRange")+
    //    ", currentlyRunningSources: "+flow.get("currentlyRunningSources")

    if(!isValueInRange(clicks, flow.get("clicksPreviousSecond"), flow.get("driftRange"))) {
        if(flow.get("isTransitioning")!==true) {
            flow.set("isTransitioning", true) //set even if already set
            flow.set("lastStableClicks", flow.get("clicksPreviousSecond"))
        }
    } else { //not in transition
        if(flow.get("isTransitioning")){ //transition has just completed
            var signature = clicks-flow.get("lastStableClicks")
            var currentlyRunningSources = flow.get("currentlyRunningSources")
            var sourceList = flow.get("sourceList")
            if(!tryFindAndHandleSourceMatch(signature, sourceList, currentlyRunningSources)) { 
                //no source match or could not handle match, so add/remove to/from unknown
                var currentlyRunningSourcesTotal = getCurrentlyRunningSourcesTotal(currentlyRunningSources)
                if(signature > 0){
                    addToUnknownSource(signature, currentlyRunningSources, currentlyRunningSourcesTotal)
                } else {
                    removeFromUnknownSource(signature, currentlyRunningSources, currentlyRunningSourcesTotal)
                }
            }

            //update global variables
            flow.set("currentlyRunningSources", currentlyRunningSources)
            flow.set("isTransitioning", false)
        }
    }
    //set "clicksPreviousSecond", signature calculation uses "lastStableClicks" so we don't have to worry about slow drifting
    flow.set("clicksPreviousSecond", clicks)
    //failsafe
    if(clicks === 0){ flow.set("currentlyRunningSources", []) }
}

function tryFindAndHandleSourceMatch(signature, sourceList, currentlyRunningSources){
    //console.log("tryFindAndHandleSourceMatch(...)")
    var found = false
    for(let i = 0; i<sourceList.length; i++){
        if(isMatch(signature, sourceList[i])){
            if(signature>0){ //position transition so add it, this is always true when called to identify "unknown" category
                found = addSource(sourceList[i], currentlyRunningSources) //found could be false if already had max number of sources
            } else { //negative transition so remove it
                found = removeSource(sourceList[i], currentlyRunningSources) //found could be false if matched source didn't exists in currentlyRunningSources so it should be taken from "unknown"
            }
            //console.log("currentlyRunningSources: "+currentlyRunningSources)
            break //found match so don't need to search anymore
        }
    } 
    return found
}

function isMatch(signature, sourceListItem){
    return isValueInRange(Math.abs(signature), sourceListItem.median, sourceListItem.range) //can't use currentDrift because reference signatures are too close
}

function isValueInRange(value, median, range){
    //console.log("isInRange(...)")
    return Math.abs(value-median)<=range
}

//adds sourceListItem into currentlyRunningSources - adds new if doesn't exist, increment quantity if it does
//returns true if successful
function addSource(sourceListItem, currentlyRunningSources, currentSourcesOn){
    //console.log("addNewSource(...)")
    let isSuccessfullyAdded = false
    //check that we don't already have max sources of this type on
    var numberOfTheseSources = getNumberOfTheseSources(sourceListItem, currentlyRunningSources)
    if(numberOfTheseSources === 0){ //if doesn't exist in currentlyRunningSources yet...
        currentlyRunningSources.push({name: sourceListItem.name, clicks: sourceListItem.median, quantity: 1})
        isSuccessfullyAdded = true
    } else { //if exists already, add another
        for(let j=0; j<currentlyRunningSources.length; j++){
            if(currentlyRunningSources[j].name === sourceListItem.name){
                isSuccessfullyAdded=true
                currentlyRunningSources[j].clicks += sourceListItem.median
                currentlyRunningSources[j].quantity++
                break
            }
        }
    }
    return isSuccessfullyAdded
}

//removes sourceListItem from currentlyRunningSources if it is present
//returns true if successful
function removeSource(sourceListItem, currentlyRunningSources, currentSourcesOn){
    //console.log("removeSource(...)")
    let isSuccessfullyRemoved = false
    for(let j=0; j<currentlyRunningSources.length; j++){
        if(currentlyRunningSources[j].name === sourceListItem.name){
            isSuccessfullyRemoved=true
            if(currentlyRunningSources[j].quantity>1){
                currentlyRunningSources[j].clicks -= sourceListItem.median
                currentlyRunningSources[j].quantity--
            } else {
                currentlyRunningSources.splice(j, 1)
            }
            break
        }
    }
    return isSuccessfullyRemoved
}

function getNumberOfTheseSources(sourceListItem, currentlyRunningSources){
    for(var j=0; j<currentlyRunningSources.length; j++){
        if(currentlyRunningSources[j].name === sourceListItem.name)
        { 
            return currentlyRunningSources[j].quantity 
        }
    }
    return 0
}

function getCurrentlyRunningSourcesTotal(currentlyRunningSources){
    let currentlyRunningSourcesTotal = 0
    for(let i=0; i<currentlyRunningSources.length; i++){
        currentlyRunningSourcesTotal += currentlyRunningSources[i].clicks
    } 
    return currentlyRunningSourcesTotal
}

//adds signature to sources "unknown" if exist, otherwise, creates source "unknown"
function addToUnknownSource(signature, currentlyRunningSources, currentlyRunningSourcesTotal){
    //console.log("addToUnknownSource(...)")
    let isSuccessfullyAdded = true
    if(currentlyRunningSources.length>0 && currentlyRunningSources[0].name==="unknown") { //if "unknown" is in array, it will always be first
        currentlyRunningSources[0].clicks += signature
    } else { //need to add "unknown" as first element in array
        currentlyRunningSources.unshift({name: "unknown", clicks: signature, quantity: 1})
    }
    return isSuccessfullyAdded
}

//removes signature from unknown if exists and removes unknown if it is zero or less, otherwise does nothing
function removeFromUnknownSource(signature, currentlyRunningSources, currentlyRunningSourcesTotal){
    //console.log("removeFromFirstSource(...)")
    let isSuccessfullyRemoved = false
    if(currentlyRunningSources.length>0 && currentlyRunningSources[0].name==="unknown"){
        currentlyRunningSources[0].clicks += signature
        if(currentlyRunningSources[0].clicks <= 0){
            currentlyRunningSources.splice(0, 1)
        }
        isSuccessfullyRemoved = true
    } else { } //no unknown source, nothing we can accurately do...
    return isSuccessfullyRemoved
}

