    start.setHours(0,0,0,0)
    end.setHours(24,0,0,0)
    try {
        await flow.get("waterDbDailyColl").updateOne(
          { created: { $gt:start, $lt:end }},
          { $pull: { "timeRecord.$[t].sources" : { name : name } } },
          { arrayFilters : [ { "t.created": date } ] })
    } catch(error) {
        console.error(error)
    }
}

