//node.send([null, null, null, {payload: msg.payload, prevFlow: flow.get("prevFlow"), margin: (flow.get("sourcesOn").length || 1)*flow.get("driftMargin"), sourcesOn: flow.get("sourcesOn")}])
//identify source
if(flow.get("inTransition")) {
    var isTransitioning = Math.abs(msg.payload-flow.get("prevFlow"))>flow.get("driftMargin")
} else {
    var isTransitioning = Math.abs(msg.payload-flow.get("prevFlow"))>((flow.get("sourcesOn").length || 1)*flow.get("driftMargin"))
}
//node.send([null, null, null, {isTransitioning: isTransitioning}])
if(isTransitioning){
    if(!flow.get("inTransition")){
        flow.set("inTransition", true)
        flow.set("transitionData", [{clicks: msg.payload, created: now, source: "unknown"}])
    } else {
        var transitionData = flow.get("transitionData")
        transitionData.push({clicks: msg.payload, created: now, source: "unknown"})
        flow.set("transitionData", transitionData)
    }
    //node.send([null, null, null, {transitionData: flow.get("transitionData")}])
    flow.set("prevFlow", msg.payload)
} else { //not in transition
    if(flow.get("inTransition")){ //transition is completed
        var transitionData = flow.get("transitionData")
        var signature = msg.payload-flow.get("lastStableFlow")
        //node.send([null, null, null, {where: "after transition", lastStableFlow: flow.get("lastStableFlow"), signature: signature}])
        var sourcesOn = flow.get("sourcesOn")
        var currentDrift = (sourcesOn.length || 1)*flow.get("driftMargin")
        var sourceList = flow.get("sourceList", "file")
        var found = false
        for(let i = 0; i<sourceList.length; i++){
            if(Math.abs((Math.abs(signature)-sourceList[i].clicks))<=flow.get("driftMargin")){//can't use currentDrift because reference signatures are too close
                var currentSourcesOn = {clicks: 0, quantity: 1}
                if(signature>0){ //position transition so add it
                    //check that we don't already have max sources of this type on
                    var numberOfTheseSources = 0
                    for(var j=0; j<sourcesOn.length; j++){
                        if(sourcesOn[j].name === sourceList[i].name)
                        { 
                            numberOfTheseSources = sourcesOn[j].quantity 
                            break
                        }
                    }
                    if(numberOfTheseSources < sourceList[i].quantity){
                        found = true
                        if(numberOfTheseSources === 0){ //if doesn't exist in sourcesOn yet...
                            sourcesOn.push({name: sourceList[i].name, clicks: sourceList[i].clicks, quantity: 1})
                        } else { //if exists already, add another
                            //node.send([null, null, null, {sourcesOn: sourcesOn, currentSourcesOn: currentSourcesOn}])
                            currentSourcesOn.clicks = sourcesOn[j].clicks
                            sourcesOn[j].clicks += sourceList[i].clicks
                            sourcesOn[j].quantity++
                            currentSourcesOn.quantity = sourcesOn[j].quantity
                            //node.send([null, null, null, {sourcesOn: sourcesOn, currentSourcesOn: currentSourcesOn}])
                        }
                    }
                } else { //negative transition so remove it
                    for(let j=0; j<sourcesOn.length; j++){
                        if(sourcesOn[j].name === sourceList[i].name){
                            flow.set("lastStableFlow", msg.payload) //set here so we can get correct clicks in transitionData
                            found=true
                            if(sourcesOn[j].quantity>1){
                                sourcesOn[j].clicks -= sourceList[i].clicks
                                sourcesOn[j].quantity--
                                currentSourcesOn.clicks = sourcesOn[j].clicks
                                currentSourcesOn.quantity = sourcesOn[j].quantity
                            } else {
                                sourcesOn.splice(j, 1)
                            }
                            break
                        }
                    }
                }
                //node.send([null, null, null, {sourcesOn: sourcesOn, currentSourcesOn: currentSourcesOn}])
                if(found===true){ //could be, if decrease, that match was found but that source was never added so it should be taken from "unknown"
                    for(let k=0; k<transitionData.length; k++){
                        transitionData[k].source = sourceList[i].name
                        transitionData[k].clicks = currentSourcesOn.clicks + transitionData[k].clicks - flow.get("lastStableFlow")
                        transitionData[k].quantity = currentSourcesOn.quantity
                    }
                }
                break
            }
        }
        if(!found) {
            for(let k=0; k<transitionData.length; k++){
                var lastStableSources = 0
                for(let i=0; i<sourcesOn.length; i++){
                    lastStableSources += sourcesOn[i].clicks
                }
                if(transitionData[k].clicks > lastStableSources){ //transition is positive: lastStableSources will update every iteration
                    if(sourcesOn.length>0 && sourcesOn[0].name==="unknown") { //if "unknown" is in array, it will always be first
                        sourcesOn[0].clicks += transitionData[k].clicks-lastStableSources
                    } else { //need to add "unknown" as first element in array
                        sourcesOn.unshift({name: "unknown", clicks: transitionData[k].clicks-lastStableSources, quantity: 1})
                    }
                    transitionData[k].clicks = sourcesOn[0].clicks
                    transitionData[k].quantity = 1
                }
                else {
                    var shouldSubtract = true
                    while(shouldSubtract){
                        if(sourcesOn.length > 0){ //transition was negative
                            sourcesOn[0].clicks = sourcesOn[0].clicks+transitionData[k].clicks-lastStableSources
                            if(sourcesOn[0].name !== "unknown" || sourcesOn[0].clicks <= 0){ //need to delete "unknown" as well if it has reached zero
                                //delete transition source entry from this transitionData to end from database
                                for(let m=k; m<transitionData.length; m++){
                                    deleteSourceFromDatabase(transitionData[m].created, sourcesOn[0].name)
                                }
                            }
                            if(sourcesOn[0].clicks <= 0){
                                //node.send([null, null, null, {where: "nut: <=0", sourcesOn: sourcesOn, lastStableSources: lastStableSources, transitionData: transitionData}])
                                //reduce lastStableSources for next subtraction
                                lastStableSources = lastStableSources+(transitionData[k].clicks-lastStableSources+Math.abs(sourcesOn[0].clicks))
                                sourcesOn.splice(0, 1)
                            }
                            else {
                                transitionData[k].clicks = sourcesOn[0].clicks
                                transitionData[k].source = "unknown"
                                transitionData[k].quantity = 1
                                sourcesOn[0].name = "unknown"
                                shouldSubtract = false
                                //node.send([null, null, null, {where: "nut: >0", sourcesOn: sourcesOn, lastStableSources: lastStableSources, transitionData: transitionData}])
                            }
                        } else { shouldSubtract = false } //to prevent infinite loop if leftovers in transitionData. what to do with leftovers in transistionData?
                    }
                }
            }
            if(msg.payload === 0) { sourcesOn = [] } //why do we need this?
        }

        //try to match "unknown" source
        if((sourcesOn.length>0 && sourcesOn[0].name==="unknown")){
            //node.send([null, null, null, {where: "match unknown code", sourcesOn: sourcesOn}])
            var foundOne = false
            for(let i = 0; i<sourceList.length; i++){
                if(Math.abs(sourcesOn[0].clicks-sourceList[i].clicks)<=flow.get("driftMargin")){//can't use currentDrift because reference signatures are too close
                    //check that we don't already have max sources of this type on
                    var numberOfTheseSources = 0, currentSourcesOn = {clicks: 0, quantity: 1}
                    for(var j=0; j<sourcesOn.length; j++){
                        if(sourcesOn[j].name === sourceList[i].name)
                        { 
                            numberOfTheseSources = sourcesOn[j].quantity 
                            break
                        }
                    }
                    if(numberOfTheseSources < sourceList[i].quantity){
                        if(numberOfTheseSources === 0){
                            sourcesOn[0].name = sourceList[i].name
                            sourcesOn[0].quantity = 1
                            currentSourcesOn.clicks = sourcesOn[0].clicks
                            currentSourcesOn.quantity = sourcesOn[0].quantity
                        } else {
                            sourcesOn[j].clicks += sourceList[i].clicks
                            sourcesOn[j].quantity++
                            currentSourcesOn.clicks = sourcesOn[0].clicks
                            currentSourcesOn.quantity = sourcesOn[0].quantity
                        }
                        foundOne = true
                        //delete last unknown from db, since we have match
                        deleteSourceFromDatabase(transitionData[transitionData.length-1].created, "unknown")
                        //change last transition data to be the new match
                        if(transitionData.length>0){
                            transitionData[transitionData.length-1].source = sourceList[i].name
                            transitionData[transitionData.length-1].clicks = currentSourcesOn.clicks
                            transitionData[transitionData.length-1].quantity = currentSourcesOn.quantity
                        }
                        break
                    }
                }
            }
        }

        //update db with transitionData
        //node.send([null, null, null, {where: "db update transition", sourcesOn: sourcesOn, transitionData: transitionData}])
        for(let l=0; l<transitionData.length; l++){
            var start = new Date(transitionData[l].created)
            var end = new Date(start.valueOf())
            start.setHours(0,0,0,0)
            end.setHours(24,0,0,0)
            if(transitionData[l].clicks <= 0){ //delete any entries that would have 0 or negative clicks
                deleteSourceFromDatabase(transitionData[l].created, transitionData[l].source)
            } else {
                flow.get("waterDbDailyColl").bulkWrite( [
                    //first updates entry if it exists.
                    { updateOne : {
                       filter : { created: { $gt:start, $lt:end } }, 
                       update : { $set : { "flow.$[t].sources.$[source].clicks": Number(transitionData[l].clicks), 
                                            "flow.$[t].sources.$[source].quantity": Number(transitionData[l].quantity) } },
                       arrayFilters : [ { "t.created": new Date(transitionData[l].created) }, { "source.name": transitionData[l].source } ] 
                    } },
                    //second update creates entry if doesn't exist. NOTE: must match the full subdocument in array element! 
                    { updateOne : {
                       filter : { created: { $gt:start, $lt:end } }, 
                       update : { $addToSet : { "flow.$[t].sources": {name: transitionData[l].source, 
                                                                    clicks: Number(transitionData[l].clicks), 
                                                                    quantity: transitionData[l].quantity }}},
                       arrayFilters : [ { "t.created": new Date(transitionData[l].created) } ]
                    } }
                ] )
            }
        }

        //update global variables
        flow.set("sourcesOn", sourcesOn)
        flow.set("lastStableFlow", msg.payload)
        flow.set("inTransition", false)
    }
    //set "prevFlow", signature calculation uses "lastStableFlow" so we don't have to worry about slow drifting
    flow.set("prevFlow", msg.payload)
    //failsafe
    if(msg.payload === 0){ flow.set("sourcesOn", []) }
}
node.send([null, null, { sources: flow.get("sourcesOn") }])

//update database with flow, gallon
