function deleteSourceFromDatabase(date, name) {
    node.send([null, null, null, {date: date, name: name}])
    var start = new Date(date)
    var end = new Date(start.valueOf())
    start.setHours(0,0,0,0)
    end.setHours(24,0,0,0)
    flow.get("waterDbDailyColl").updateOne(
      { created: { $gt:start, $lt:end }},
      { $pull: { "flow.$[t].sources" : { name : name } } },
      { arrayFilters : [ { "t.created": date } ] },
      function(err, result) {
        if(err){ node.error(err) } 
        else {
            node.send([null, null, null, {where: "the delete", result: result}])
        }
      }
    )
}

