var start = new Date()
var end = new Date(start.getTime())
start.setHours(0,0,0,0)
end.setHours(24,0,0,0)
//get first doc prior to today which contains an average field, only return the average and naverage fields
flow.get('waterDbDailyColl').findOne({created: { $lt:start }}, /*{gallons: 1, average: 1, numavg: 1},*/ //need flow for averageDay
    { sort: { _id: -1 }, limit: 1 }, function(err, doc) {
    if(err){ node.error(err) } 
    else {
        if(doc){
            var newdoc = {  
                $setOnInsert: { 
                    created: new Date(), 
                    gallons: 0
                },
                $currentDate: { modified: true }
            }
            if(!doc.incomplete) {
                if(doc.average){
                    newdoc.$set = { 
                        average : ((doc.average*doc.numavg)+doc.gallons)/(doc.numavg+1),
                        numavg: doc.numavg+1
                    }  
                } else {
                    newdoc.$set = { 
                        average : doc.gallons, 
                        numavg: 1
                    } 
                }
                if(doc.flow){
                    var begin = new Date()
                    updateAverageDay(doc.flow)//takes ~0.1ms per element on ubuntu machine, up to 8.5sec for full flow array
                    var end = new Date()
                    node.warn("updating averageDay took: "+end.getTime()-begin.getTime()+"ms.")
                }
            } else { //doc incomplete
                if(doc.average){ // don't use that day's gallon data, just copy the average over
                    newdoc.$set = { 
                        average : doc.average, 
                        numavg : doc.numavg
                    }  
                } else { //if incomplete and no average, can't create an average
                    node.send({payload: "new day", status: "incomplete and no average"})
                    return
                }
            }
            flow.get('waterDbDailyColl').updateOne({created: { $gt:start, $lt:end }}, newdoc, {upsert:true, w: 1}, function(err, result) {
                if(err){ node.error(err) } 
                else {
                    node.send({payload: "new day", status: result})
                }
            })
        } else { //database has no previous documents
            node.send({payload: "new day", status: "no doc"})
        }
    }
})

function updateAverageDay(flowData){
    var averageDay
    var averageDay = flow.get("averageDay", "file")
    var thisDayStr = flowData[0].created
    var thisDay = new Date(thisDayStr)
    thisDay.setHours(0,0,0,0)
    var thisDayStart = thisDay.getTime()
    var currentIndex = 0
    var gallonsSoFar = 0
	for(let second=1; second <= 86400; second++){
	    var foundOne = true
		while(foundOne == true){
		    foundOne = false
    		if(currentIndex<flowData.length){
		        var currentTimestamp = new Date(flowData[currentIndex].created)
    		    if(second*1000 > (currentTimestamp - thisDayStart)){
    				gallonsSoFar += flow.get("clicksToGallons")(flowData[currentIndex].clicks)
    				foundOne = true
    				currentIndex++
    			}
    		}
		}
		//all entries for current second have been tallied.  Now add it to average.
		averageDay.timeline[second-1] = (averageDay.timeline[second-1]*averageDay.numAvg+gallonsSoFar)/(averageDay.numAvg+1)
	}
	averageDay.average = averageDay.timeline[averageDay.timeline.length-1]
	averageDay.numAvg++
    flow.set("averageDay", averageDay, "file")
}

