//get last date we checked
var waterDateLastSeen
var waterDateLastSeenStr = flow.get("waterDateLastSeen", "file")
if (!waterDateLastSeenStr){
    waterDateLastSeen = new Date()
} else {
    waterDateLastSeen = new Date(waterDateLastSeenStr)
}

const now = new Date()

//if new day
var newDay = null
if (!(waterDateLastSeen.getDate() == now.getDate() &&
    waterDateLastSeen.getMonth() == now.getMonth() &&
    waterDateLastSeen.getFullYear() == now.getFullYear())){
    newDay = {newDay: true}
}

//timeout
var todayIncomplete = null
var lastSeenIncomplete = null
if((now.getTime()-waterDateLastSeen.getTime())>flow.get("TimeoutMs")){
    //if more than timeout since midnight
    var lastMidnight = new Date(now.getTime())
    lastMidnight.setHours(0,0,0,0)
    if ((now.getTime()-lastMidnight.getTime())>flow.get("TimeoutMs")){
        todayIncomplete = true
    }
    if (newDay) {
        var waterDateLastSeenMidnight = new Date(waterDateLastSeen.getTime())
        waterDateLastSeenMidnight.setHours(24,0,0,0)
        if((waterDateLastSeenMidnight.getTime()-waterDateLastSeen.getTime())>flow.get("TimeoutMs")){
            //todo: find doc for date last seen
            //todo: if exists, update as incomplete
            lastSeenIncomplete = true
        }
    }
}

//update date last seen to now
flow.set("waterDateLastSeen", now, "file")

if(msg.payload !== 0 || todayIncomplete) {
    var waterDbDailyColl = flow.get('waterDbDailyColl')
    //search for today's doc
    start = new Date(now)
    end = new Date(start.valueOf())
    start.setHours(0,0,0,0)
    end.setHours(24,0,0,0)
    var doc = {
        $setOnInsert: {
            created: now
        },
        $currentDate: {modified: true}
    }
    if(msg.payload !== 0){
        doc.$inc =  { gallons: flow.get('clicksToGallons')(msg.payload) },
        doc.$push = { flow: { clicks: Number(msg.payload), created: new Date() }}
    }
    if(todayIncomplete){
        doc.$set = { incomplete : true }
    }
    waterDbDailyColl.update({created: { $gt:start, $lt:end }}, doc, {upsert:true, w: 1}, function(err, result) {
        if(err){ node.error(err) } 
        else {
            waterDbDailyColl.findOne({created: { $gt:start, $lt:end }}, {gallons: 1}, function(err, doc) {
                if(err){ node.error(err) } 
                else {
                    node.send([null, {payload: doc.gallons}])
                }
            })  
        }
    })
}

